from __future__ import absolute_import, division, print_function

import json

import etcd
import pytest

from appr.exception import InvalidRelease, UnableToLockResource
from appr.models.kv.etcd.blob import Blob
from appr.models.kv.etcd.package import Package


class MockEtcdResult(object):
    def __init__(self, path, data):
        self.path = path
        self.data = data

    @property
    def key(self):
        return self.path

    @property
    def value(self):
        return json.dumps(self.data)


class MockEtcdResults(object):
    def __init__(self, results):
        self.results = results

    @property
    def children(self):
        return [MockEtcdResult(x, 'value') for x in self.results]


