from __future__ import absolute_import, division, print_function

import json

import etcd
import pytest

from appr.exception import (InvalidRelease, PackageAlreadyExists,
                            UnableToLockResource)
from appr.models.kv.etcd.package import Package


@pytest.fixture()
def release_data():
    releases = ['1.3.0', '1.3.2-rc2', '1.8.2-rc2', '1.4.2', '1.0.0', '1.2.0']
    data = {'channels': {}, "releases": {}}
    for release in releases:
        data['releases'][release] = {"channels": [], "manifests": {"kpm": {"package": "titi/rocketchat", "created_at": "2016-10-11T12:03:24.941731", "blob": "", "release": release, "media_type": "kpm", "digest": "d3b54b7912fe770a61b59ab612a442eac52a8a5d8d05dbe92bf8f212d68aaa80"}}}
    return data

def test_check_data_validrelease():
    assert Package.check_release("1.4.5-alpha") is None


def test_check_data_invalidrelease():
    with pytest.raises(InvalidRelease):
        assert Package.check_release("1.4.5a-alpha")

